mapper.select:
SELECT MEMBER_ID, MEM_NAME, MEM_PW, MEM_ADDR, MEM_BIRTH, MEM_PHONE, MEM_EMAIL 
FROM MEMBERLIST 
	WHERE MEMBER_ID = #{memberId}

  SELECT COUNT(*) 
	FROM MEMBERLIST
		WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBERLIST
SET MEM_PW=#{memPw}, MEM_ADDR=#{memAddr}, MEM_PHONE=#{memPhone}, MEM_EMAIL=#{memEmail}
WHERE MEMBER_ID= #{memberId}
