mapper.select:
SELECT MEMBER_ID, MEM_PW 
FROM MEMBERLIST 
	WHERE MEMBER_ID = #{memberId}

  SELECT COUNT(*) 
	FROM MEMBERLIST
		WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE ADMIN.MEMBERLIST
SET MEM_NAME= #{mem_name}, MEM_PW=#{mem_pw}, MEM_ADDR=#{mem_addr}, MEM_PHONE=#{mem_phone}, MEM_EMAIL=#{mem_email}
WHERE MEMBER_ID={memberId}
